    private void KdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_KdActionPerformed
        // TODO add your handling code here:
        String id = Kd.getText();
        String harga = eHarga.getText();
        int hrg = 0;
        if (id.equals("5")){
            Nb.setText("steering");
            eHarga.setText("Rp1,000.000");
            hrg = 1000000;
        }else{
            Nb.setText("gak ada");
            eHarga.setText("Rp 0");
            hrg = 0;
        }
        eHarga.setText(Integer.toString(hrg));
        
    }//GEN-LAST:event_KdActionPerformed

