    private void bSumbitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bSumbitActionPerformed
        // TODO add your handling code here:
        String id = Kd.getText();
        String nama = Nb.getText();
        String harga = eHarga.getText();
        double bnyk = Double.parseDouble(qty.getText());
        String total = eTotal.getText();
        
        eData.setText("Kd Barang : "+id+"\n"+"Nama Barang : "+nama+"\n"+"Harga Barang : "
                +harga+"\n"+"Banyak Barang : "+bnyk+"\n"+"Total : "+total);
    }//GEN-LAST:event_bSumbitActionPerformed

